--
-- Create procedure `updateOperator`
--
CREATE
DEFINER = 'root'@'localhost'
PROCEDURE IF NOT EXISTS updateOperator (IN id int UNSIGNED, IN dni int UNSIGNED, IN name varchar(20), IN familyname varchar(30))
DETERMINISTIC
BEGIN
  #START TRANSACTION;

  IF (existOperator(id) = TRUE) THEN
    UPDATE LOW_PRIORITY operator o
    SET o.dni = dni,
        o.name = name,
        o.familyname = familyname
    WHERE o.id = id;

    SELECT
      'Success!' AS `response`;
  ELSE
    SELECT
      'Unsuccess!' AS `response`;
  END IF;

#COMMIT;
END
$$

--
-- Create procedure `deleteOperator`
--
CREATE
DEFINER = 'root'@'localhost'
PROCEDURE IF NOT EXISTS deleteOperator (IN id int UNSIGNED)
BEGIN

  IF (existOperator(id) = TRUE) THEN
    DELETE LOW_PRIORITY QUICK
      FROM operator o
    WHERE o.id = id
    LIMIT 1;

    SELECT
      'Success!' AS `response`;
  ELSE
    SELECT
      'Unsuccess!' AS `response`;
  END IF;

END
$$

